import React, { Component } from 'react';

export default class App extends Component {

  constructor(){
    super();

    this.state = {
      user: [],
      showUsers: false,
    }
  }

  async componentDidMount(){ // requisicoes http por que só é executado apenas uma vez no componente
    const res = await fetch (
      'https://randomuser.me/api/?seed=rush&nat=br&results=10'
    );

    const json = await res.json();

    this.setState({
      users: json.results,
    });
  }

  componentDidUpdate(){
    console.log(' componentDidUpdate de App.js')
  }

  componentWillMount(){
    console.log(' componentWillMount de App.js')
  }

  handleShowUsers = (event) => {
    this.setState({showUsers: event.target.value});
  }

  render() {

    return (

    <div>
      <div className="switch">

        <label>
          Mostrar usuários:
          <input type="checkbox" onChange={this.handleShowUsers}/>
          <span className="lever"></span>
        </label>
      </div>

       <hr />
       <div>User</div>
     </div>

    )
  }
}
