import React, { Component } from 'react';
//import React, { useState } from 'react';
import ProjetoBase from './components/ProjetoBase/ProjetoBase';
import {getNewTimestamp} from './components/timestamp';

export default function App () {
  /* definindo estado da aplicação */
   const [clickArray, setClickArray] = useState([]);

  const handleClick =  () => {
     const newClickArray = Object.assign([] , clickArray);
     newClickArray.push(getNewTimestamp());

     setClickArray(newClickArray)
   }

    return (
      <div>
        <h1>
          React com <em>Hook</em>
        </h1>

        <button onclick = {handleClick}>Click aqui</button>

        <ul>
          {clickArray.map((item)=> {
            return <li key = {item} > {item}</li>
          })}
        </ul>

      </div>
    );

}
